mapper.select:

SELECT a.item_no, a.item_name, a.item_size, a.item_price ,a.image_uuid
FROM (
    SELECT a.*, rownum rn
    FROM (
        SELECT item_no, item_name, item_size, item_price, image_uuid
        FROM itemgiftcard
        ORDER BY item_no DESC
    ) a
    WHERE rownum <= #{end}
) a
WHERE rn >= #{start}

SELECT
count(*) FROM itemgiftcard where item_no =
#{itemNo}
